def format_sympy_expr(f_expr):
    """SymPy ÏãùÏùÑ LaTeX ÌòïÏãùÏúºÎ°ú Î≥¥Í∏∞ Ï¢ãÍ≤å Î≥ÄÌôòÌï©ÎãàÎã§."""
    # LaTeX ÌòïÏãùÏúºÎ°ú Î≥ÄÌôò (SymPyÏùò Í∏∞Î≥∏ LaTeX Ìï®Ïàò ÏÇ¨Ïö©)
    f_str = f_expr.args[0] if f_expr.is_Equality else f_expr
    latex_str = str(f_str).replace('**', '^').replace('*', ' \cdot ')
    
    # Î∂ÑÎ™®Í∞Ä ÏûàÎäî Ìï≠Ïóê \frac{} Ï†ÅÏö© (Îß§Ïö∞ Îã®ÏàúÌôîÎêú Î≤ÑÏ†Ñ)
    parts = []
    for term in str(f_str).split(' '):
        if '/' in term:
            try:
                coeff, rest = term.split('*', 1)
                num, den = coeff.split('/')
                parts.append(f'\\frac{{{num}}}{{{den}}} {rest}')
            except:
                parts.append(term)
        else:
             parts.append(term)
    
    # ÏµúÏ¢ÖÏ†ÅÏúºÎ°ú Ï∂úÎ†•Îê† LaTeX Î¨∏ÏûêÏó¥ÏùÑ Ïû¨Ï°∞Î¶ΩÌï©ÎãàÎã§.
    return f"f(x) = {f_expr.as_ordered_terms()}"

def generate_easy_polynomial(degree):
    """ÎØ∏Î∂Ñ Ïãú Ïù∏ÏàòÎ∂ÑÌï¥Í∞Ä Ïâ¨Ïö¥ Ï†ïÏàò Í≥ÑÏàò Îã§Ìï≠ Ìï®ÏàòÎ•º ÏÉùÏÑ±Ìï©ÎãàÎã§."""
    x = symbols('x')
    
    # 3Ï∞® Ìï®Ïàò
    if degree == 3:
        roots = sorted(random.sample(range(-3, 4), 2))
        a_prime = random.choice([1, 2, -1, -2])
        f_prime_expr = 3 * a_prime * (x - roots[0]) * (x - roots[1])
        f_expr = f_prime_expr.integrate(x) + random.randint(-5, 5) 
        
    # 4Ï∞® Ìï®Ïàò
    elif degree == 4:
        roots = sorted(random.sample(range(-2, 3), 3))
        a_prime = random.choice([1, -1]) 
        f_prime_expr = 4 * a_prime * (x - roots[0]) * (x - roots[1]) * (x - roots[2])
        f_expr = f_prime_expr.integrate(x) + random.randint(-4, 4)
        
    else:
        raise ValueError("3Ï∞® ÎòêÎäî 4Ï∞® Ìï®ÏàòÎßå ÏßÄÏõêÎê©ÎãàÎã§.")
    
    # SymPyÏùò latex Ìï®ÏàòÎ•º ÏÇ¨Ïö© (Í∞ÄÏû• ÏïàÏ†ÑÌï®)
    from sympy.printing.latex import print_latex
    from io import StringIO
    
    output = StringIO()
    print_latex(f_expr, output)
    latex_str = output.getvalue().replace('\n', '').strip()
    
    return f"f(x) = {latex_str}", f_expr

